mapper.select:
SELECT user_id, name, password, birth_date, phone_number, gender
FROM users.users
WHERE user_id = #{id.value}

SELECT user_id, name, password
FROM users.users
ORDER BY user_id;

mapper.update:
UPDATE users.users SET
  name = #{user.name.text},
  birth_date = #{user.dateOfBirth.date},
  phone_number = #{user.phoneNumber.text},
  gender = #{user.gender}
WHERE user_id = #{user.id.value};
